<script src="https://ajax.googleapis.com/ajax/libs/prototype/1.6.0.3/prototype.js"></script>
<script type="text/javascript">
function main() {
	var prefs = new gadgets.Prefs();

	var height = prefs.getInt("height");
	gadgets.window.adjustHeight( ( height > 0 ? height : undefined ) );

	var itemDisplay = prefs.getString("itemDisplay");
	var target;
	switch(itemDisplay){
		case "newwindow":
			target = "_blank";break;
		case "inline":
			target = "ifrm";break;
		default:
			target = "";break;
	}
	var listener = function() {
		try {
			this.target = target;
			if( !this.href )
				this.href = this.action;

			if( itemDisplay != "newwindow")
				parent.IS_Portal.buildIFrame( this );
		} catch( ex ) {
			gadgets.warn( ex );
		}
	};

	var anchors = document.getElementsByTagName("a");
	for( var i=0;i<anchors.length;i++ ) {
		var anchor = anchors[i];
		anchor.target = "_blank";
		Event.observe( anchor,"click",listener );
	}

	var forms = document.getElementsByTagName("form");
	for( var i=0;i<forms.length;i++ ) {
		var form = forms[i];
		form.target = "_blank";
		Event.observe( form,"submit",listener );
	}
}

gadgets.util.registerOnLoadHandler( main );
</script>
